import Layout from "../../components/Layout";
import HelloSection from "../index/components/HelloSection";
import AboutSection from "../index/components/AboutSection";
import AffairsSection from "./components/AffairsSection/AffairsSection";
import ContactSection from "./components/ContactSection/ContactSection";
import Spinner from "../../components/Spinner";
import "../../styles/index.css";
import { useEffect, useState } from "react";

const client = require("contentful").createClient({
  space: "sc9ok02q0vxo",
  accessToken: "wWLPUyf9LFpFrck9Ke7ZBcZj5g2OvicRPB7ET3VyROQ"
});

export default () => {
  async function fetchEntriesForContentType(contentType) {
    const entries = await client.getEntries({
      content_type: contentType
    });
    if (entries.items) return entries.items;
    console.log(`Error getting Entries for ${contentType.name}.`);
  }

  const [posts, setPosts] = useState([]);

  useEffect(() => {
    async function getPosts() {
      const allPosts = await fetchEntriesForContentType("about");
      setPosts(allPosts);
    }
    getPosts();
  }, []);

  console.log(posts);
  if (posts.length === 0) {
    return <Spinner />;
  } else {
    return (
      <Layout>
        <HelloSection />
        <AboutSection
          title={posts[0].fields.title}
          intro={posts[0].fields.intro.content[0].content[0].value}
          info={posts[0].fields.info.content[0].content[0].value}
        />
        <span className="main__dash w-full block" />
        <AffairsSection />
        <span className="main__dash w-full block" />
        <ContactSection />
        <p>{posts[0].fields.title}</p>
      </Layout>
    );
  }
};
